import React, { useState } from 'react';
import Search from './search';
import styled from 'styled-components';
import { inject, observer } from 'mobx-react';
import { toJS } from 'mobx';

const Results = styled('div')`
    display: flex;
    width: 100%;
    flex-wrap: wrap;
    height: 100vh;
    justify-content: space-between;
    >img {
        width: 32%;
    }
`;

const SearchResults = ({ ...props }) => {
    const [query, setSearchQuery] = useState('');
    const [results, setSearchResults] = useState([])
    const [imgPath, setImagePath] = useState([]);

    const handleSearch = async () => {
        await props.searchStore.getSearchData(query);
        setImagePath(toJS(props.searchStore.imageData));
        setSearchResults(toJS(props.searchStore.searchData));
    }
    console.log('results', results);
    return (
        <>
            <Search setSearchQuery={setSearchQuery} handleSearch={handleSearch}/>
            <Results>
                {results.length !== 0 ? 'Search results: ' + query : ''}
                {imgPath.map((img, i) => <><div>{results[i].listprice.amount}</div><img src={img} /></>)}
            </Results>
        </>
    )
}

export default inject("searchStore")(observer(SearchResults));
